import React from 'react';
import { useState, useEffect } from 'react';
import View from './View'

const getData = () => {
    const data = localStorage.getItem('jobs');
    if (data) {
        return JSON.parse(data);
    } else {
        return []
    }
}

const Storage = () => {

    const [jobs, setjobs] = useState(getData());

    const [task, setTask] = useState('');


    const handleAdd = (e) => {
        e.preventDefault();

        let job = {
            id: 1,
            task: task
        }
    

    setjobs([...job, jobs]);
    setTask('');
}

const deleteJob = (id) => {
    const removeItem = jobs.filter((element, index) => {
        return element.id !== id
    })

    setjobs(removeItem)
}

useEffect(() => {
    localStorage.setItem('jobs', JSON.stringify(jobs))
}, [jobs])



return (
    <div>
        {/* <img 
                 className='pencil_img' 
       src={pencilimg}
       /> */}
        <form className='search' onSubmit={handleAdd}>

            <input type="text"
            className='taskbar'

                placeholder="add job"
                onChange={(e) => setTask(e.target.value)}
                value={task}
>

</input>
           

        </form>
        <View jobs={jobs} deleteJob={deleteJob} />
    </div>
)
}





export default Storage
