import React from 'react';
import './todo.css';
import { useState, useEffect } from "react";
import pencilimg from '../../Assets/redpencil.png';


const Storage = () => {

  const [tasks, setTasks] = useState(() => {

    const savedTasks = localStorage.getItem("tasks");
    if (savedTasks) {
      return JSON.parse(savedTasks);
    } else {
      return [];
    }
  });
  const [task, setTask] = useState("");
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  function handleInputChange(e) {
    setTask(e.target.value);
  }
  function handleFormSubmit(e) {
    e.preventDefault();

    if (task !== "") {
      setTasks([
        ...tasks,
        {
          id: tasks.length + 1,
          text: task.trim()
        }
      ]);
    }

    setTask("");
  }

  
  function handleDeleteClick(id) {
    const removeItem = tasks.filter((task) => {
      return task.id !== id;
    });
    setTasks(removeItem);
  }
  function handleEditClick(id) {
    const editItem = tasks.find((task) => {
      return task.id === id;
    }); 

    }
    
  
  return (
    <div>
      <img
        className='pencil_img'
        src={pencilimg}
      />
      <form className='search' onSubmit={handleFormSubmit}>

        <input className='taskbar'
          name="imput_task"
          type="text"
          placeholder="add todo"
          value={task}
          onChange={handleInputChange}
        />
      </form>
      <ul className="task-list">
        {tasks.map((task) => (
          <li key={task.id}> {task.text} 
          <button className='edit' onClick={() => handleEditClick(task.id)}>edit</button>
          <button className='delete' onClick={() => handleDeleteClick(task.id)}>delete</button></li>

        ))}
      </ul>
    </div>
  );

};




export default Storage;
