import './todo.css';
import { useState, useEffect } from "react";



const Storage = () => {
   function useLocalState() {
    const [task, setTask] = useState(localStorage.getItem(localItem));
    return [task, setTask]; 
   }

   const [task, setTask] = useLocalState()
    
