import './todo.css';
import { useState, useEffect } from "react";


const Storage = (props) => {
    const [task, setTask] = useState([{
        text: 'do dishes', 
        completed: false
},   {text: 'do dishes', 
completed: false}
]) 
    
      
    return (

      useEffect(() => {
            localStorage.setItem("task", JSON.stringify(task))  
        }, [task]) 
   
   
