import React from 'react';
import { useState, useEffect } from 'react';
import View from './View'
import pencilimg from '../../Assets/redpencil.png';


const Storage = () => {

    const [tasks, setTasks] = useState(() => {

        const savedTasks = localStorage.getItem("tasks");
        if (savedTasks) {
            return JSON.parse(savedTasks);
        } else {
            return [];
        }
    });
    const [task, setTask] = useState("");


    useEffect(() => {
        localStorage.setItem("tasks", JSON.stringify(tasks));
    }, [tasks]);

    function handleInputChange(e) {
        setTask(e.target.value);
    }
    function handleFormSubmit(e) {
        e.preventDefault();

        if (task !== "") {
            setTasks([
                ...tasks,
                {
                    id: tasks.length +1,
                    text: task
                }
            ]);
        }

        setTask("");
    }

    function handleDeleteClick(id) {
        const removeItem = tasks.filter((task) => {
            return task.id !== id;
        });
        setTasks(removeItem);
    }

    const updateTask = (id) => {
        const newTodoItems = [...tasks];
        let newItem = prompt(`Update ${task.id}?`, task.text);
        let todoObj = { text: newItem };
        newTodoItems.splice(id, 1, todoObj);
        if (newItem === null || newItem === "") {
        return;
        } else {
        id.task = newItem;
        }
        setTasks(newTodoItems);
        };
    return (
        <div>
            <img
                className='pencil_img'
                src={pencilimg}
            />
            <form className='search' onSubmit={handleFormSubmit}>

                <input className='bar'
                    name="task"
                    type="text"
                    placeholder="add todo"
                    value={task}
                    onChange={handleInputChange}
                />
            </form>
            <ul className="task-list">
                {tasks.map((task) => (
                    <li key={task.id}> {task.text} <button onClick={() => handleDeleteClick(task.id)}>X</button>
                    <button onClick={() => updateTask(task.id)}>edit</button></li>

                ))}
            </ul>
        </div>
    );

};




export default Storage;
