import React from 'react';
import './todo.css';
import { useState, useEffect } from "react";


const Storage = (props) => {
    const [task, setTask] = useState([{
        text: 'do dishes',
        completed: false
    }, {
        text: 'mop',
        completed: false
    }
    ])

 
        return (
            <form>
              <input
                type="text"
                onChange={(e) => setTask(e.target.value)}
                placeholder="add to-do"
                aria-label="fullname"
                />
                <input type="submit" value="Add"></input>
              </form>
            );
          };
        

export default Storage;
